import {
  SearchBar,
  SearchForm,
  SearchFormButton,
  SearchFormInput,
} from './styled';
import { Formik, Form, Field } from 'formik';
import { GoSearch } from 'react-icons/go';

export const Searchbar = () => {
  return (
    <SearchBar>
      <Formik
        initialValues={{ searchedTerm: '' }}
        onSubmit={(values, actions) => {
          console.log(values);
          actions.resetForm();
        }}
      >
        <Form>
          <SearchForm>
            <SearchFormButton type="submit">
              <GoSearch size={'23px'} />
            </SearchFormButton>
            <Field
              as={SearchFormInput}
              type="text"
              name="searchedTerm"
              autoComplete="off"
              autoFocus
              placeholder="Search images and photos"
            />
          </SearchForm>
        </Form>
      </Formik>
    </SearchBar>
  );
};
