import axios from 'axios';

axios.defaults.baseURL = 'https://pixabay.com';
const API_KEY = '35232464-dd394eb40b88e49b2f7bb554e';

export const getImages = async () => {
  const response = await axios.get(
    '/api/?q=cat&page=1&key=`${35232464-dd394eb40b88e49b2f7bb554e}`&image_type=photo&orientation=horizontal&per_page=12'
  );
  return response.data;
};
