import {
  SearchBar,
  SearchForm,
  SearchFormButton,
  SearchFormInput,
} from './styled';
import { Formik, Form, Field } from 'formik';
import { GoSearch } from 'react-icons/go';

export const Searchbar = () => {
  return (
    <SearchBar>
      <Formik>
        <Form>
          <SearchForm>
            <SearchFormButton type="submit">
              <GoSearch size={'23px'} />
            </SearchFormButton>
            <Field
              as={SearchFormInput}
              type="text"
              name="searchTerm"
              autoComplete="off"
              autoFocus
              placeholder="Search images and photos"
              validate={validateSearchTerm}
            />
          </SearchForm>
        </Form>
      </Formik>
    </SearchBar>
  );
};
