import { Component } from 'react';

export class ImageGallery extends Component {
  async componentDidUpdate(prevProps, prevState) {
    if (prevProps.searchedImg !== this.props.searchedImg) {
      console.log(prevProps.searchedImg, 'prevProps');
      console.log(this.props.searchedImg, 'this.props.searchedImg');
      console.log('змінилася картинка');
      const response = await API.getImages();
      this.setState({ images: response.hits });
    }
  }
  render() {
    return (
      <ul>
        {this.props.items.map(({ id, webformatURL, largeImageURL, tags }) => {
          return (
            <li key={id}>
              <img src={webformatURL} alt={tags} />
            </li>
          );
        })}
      </ul>
    );
  }
}
