import { Formik } from 'formik';
import { Input, SearchForm, FormButton, FormWrap } from './styled';
import { GoSearch } from 'react-icons/go';
import PropTypes from 'prop-types';

export const SearchBar = ({ onSubmit, isSubmitting }) => {
  const handleSubmit = (values, actions) => {
    if (values.searchedImg.trim() === '') {
      return alert('Порожня строка');
    }
    onSubmit(values).then(() => actions.setSubmitting(false));
    actions.resetForm();
  };

  return (
    <FormWrap>
      <header class="searchbar">
        <form class="form">
          <button type="submit" class="button">
            <span class="button-label">Search</span>
          </button>

          <input
            class="input"
            type="text"
            autocomplete="off"
            autofocus
            placeholder="Search images and photos"
          />
        </form>
      </header>
    </FormWrap>
  );
};

SearchBar.propTypes = {
  onSubmit: PropTypes.func.isRequired,
  isSubmitting: PropTypes.bool.isRequired,
};

<header class="searchbar">
  <form class="form">
    <button type="submit" class="button">
      <span class="button-label">Search</span>
    </button>

    <input
      class="input"
      type="text"
      autocomplete="off"
      autofocus
      placeholder="Search images and photos"
    />
  </form>
</header>;
