import React, { Component } from 'react';
import { SearchBar } from './Searchbar/SearchBar';
import { ImageGallery } from './ImageGallery/ImageGallery';
// import { PixabayAPI } from 'services/Api';

// const pixabayAPI = new PixabayAPI();

export class App extends Component {
  state = {
    images: [],
    searchedImg: '',
  };

  handleFormSubmit = searchedImg => {
    this.setState({ searchedImg });
  };

  async componentDidUpdate(prevProps, prevState) {
    if (prevProps.searchedImg !== this.state.searchedImg) {
      console.log(prevProps);
      console.log(this.state.searchedImg);
    }
    // pixabayAPI.query = this.state.searchedImg.trim();
    // const response = await API.getImages();
    // this.setState({ images: response.hits });
  }

  render() {
    return (
      <div
        style={{
          height: '100vh',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          fontSize: 40,
          color: '#010101',
        }}
      >
        <SearchBar onSubmit={this.handleFormSubmit} />

        {this.state.images.length > 0 ? (
          <ImageGallery
            searchedImg={this.state.searchedImg}
            items={this.state.images}
          />
        ) : null}
      </div>
    );
  }
}
