import {
  SearchBar,
  SearchForm,
  SearchFormButton,
  SearchFormInput,
} from './styled';
import { Formik, Field } from 'formik';
import { GoSearch } from 'react-icons/go';

export const Searchbar = () => {
  return (
    <SearchBar>
      <Formik
        initialValues={{ searchedTerm: '' }}
        onSubmit={(values, actions) => {
          console.log(values);
          actions.resetForm();
        }}
      >
        <SearchForm>
          <SearchFormButton type="submit" name="submit">
            <GoSearch size={'23px'} />
          </SearchFormButton>
          <Field
            as={SearchFormInput}
            type="text"
            name="searchedTerm"
            autoComplete="off"
            autoFocus
            placeholder="Search images and photos"
          />
        </SearchForm>
      </Formik>
    </SearchBar>
  );
};
