import { Component } from 'react';
// import { Image } from 'components/Image';
import { StyledGallery } from './styled';
import axios from 'axios';

const APIKey = '35232464-dd394eb40b88e49b2f7bb554e';
const baseAPI = 'https://pixabay.com';

export class ImageGallery extends Component {
  state = {
    images: [],
  };
  componentDidUpdate(prevProps, prevState) {
    if (prevProps.imageName !== this.props.imageName) {
      axios
        .get(`${baseAPI}/api/`, {
          params: {
            q: this.props.imageName,
            page: 1,
            per_page: 12,
            key: APIKey,
            image_type: 'photo',
            orientation: 'horizontal',
            safesearch: 'true',
          },
        })
        .then(images => {
          this.setState({ images: images.data.hits });
        })
        .catch(error => {
          console.log(error);
        });
    }
  }

  render() {
    return (
      <StyledGallery>
        <h3>{this.props.imageName}</h3>
        {/* {this.props.items.map(({ id, webformatURL, largeImageURL, tags }) => {
          return (
            <Image
              key={id}
              smallImgUrl={webformatURL}
              bigImgUrl={largeImageURL}
              imgDescr={tags}
            />
          );
        })} */}
      </StyledGallery>
    );
  }
}
