import React, { Component } from 'react';
import { SearchBar } from './Searchbar/SearchBar';
import { ImageGallery } from './ImageGallery/ImageGallery';
// import * as API from 'services/Api';

export class App extends Component {
  state = {
    images: [],
    searchedImg: '',
  };

  handleFormSubmit = searchedImg => {
    // console.log(searchedImg);
    this.setState({ searchedImg });
  };

  async componentDidUpdate(prevProps, prevState) {
    if (prevProps.searchedImg !== this.props.searchedImg) {
      console.log(prevProps.searchedImg, 'prevProps');
      console.log(this.props.searchedImg, 'this.props.searchedImg');
      console.log('змінилася картинка');
    }
  }

  render() {
    return (
      <div
        style={{
          height: '100vh',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          fontSize: 40,
          color: '#010101',
        }}
      >
        <SearchBar onSubmit={this.handleFormSubmit} />

        {/* {this.state.images.length > 0 ? ( */}
        <ImageGallery
          searchedImg={this.state.searchedImg}
          items={this.state.images}
        />
        {/* ) : null} */}
      </div>
    );
  }
}
