import React, { Component } from 'react';
import { Formik, Form, Field } from 'formik';

export class SearchBar extends Component {
  state = {
    searchedImg: '',
  };

  handleNameChange = event => {
    this.setState({ searchedImg: event.currentTarget.value.toLowerCase() });
  };

  handleSubmit = event => {
    // event.preventDefault();
    if (this.state.searchedImg.trim() === '') {
      return alert('Порожня строка');
    }
    this.props.onSubmit(this.state.searchedImg);
    this.setState({ searchedImg: '' });
  };
  render() {
    return (
      <Formik initialValues={{ searchedImg: '' }} onSubmit={this.handleSubmit}>
        <Form>
          <button type="submit" className="button">
            <span className="button-label">Search</span>
          </button>

          <Field
            type="text"
            placeholder="Search images and photos"
            name="searchedImg"
            value={this.state.searchedImg}
            onChange={this.handleNameChange}
          />
        </Form>
      </Formik>
    );
  }
}
